INSERT INTO `menu` (`menu_Id`, `user_Id`, `hashtag`, `menu_Name`, `menu_Description`, `enable`) VALUES
(2, 1, '#beo', 'Thực đơn giảm cân', 'Calo nạp vào là lượng calo bạn có được từ thức phẩm sau khi ăn, trong khi calo tiêu thụ là lượng calo mà bạn sẽ đốt cháy.  Qúa trình tiêu thụ calo xảy ra khi quá trình trao đổi chất hoạt động, tiêu hóa và vận động. Cân nặng của bạn ổn định khi lượng calo nạp vào tương đương với calo tiêu thụ.  Do vậy, để giảm cân, bạn cần ăn ít calo hơn. Việc tính lượng calo cần thiết cho cơ thể trở nên quan trọng hơn bao giờ hết để xác định bạn sẽ cần bao nhiêu calo rồi cắt giảm đi để nhắm tới mục đích giảm cân.', '1'),
(3, 1, '#gay', 'Thực đơn tăng cân', 'Calo nạp vào là lượng calo bạn có được từ thức phẩm sau khi ăn, trong khi calo tiêu thụ là lượng calo mà bạn sẽ đốt cháy.  Qúa trình tiêu thụ calo xảy ra khi quá trình trao đổi chất hoạt động, tiêu hóa và vận động. Cân nặng của bạn ổn định khi lượng calo nạp vào tương đương với calo tiêu thụ.  Do vậy, để giảm cân, bạn cần ăn ít calo hơn. Việc tính lượng calo cần thiết cho cơ thể trở nên quan trọng hơn bao giờ hết để xác định bạn sẽ cần bao nhiêu calo rồi cắt giảm đi để nhắm tới mục đích giảm cân.', '1'),
(6, 1, '#ankieng', 'Bữa ăn sáng lành mạnh', 'Calo nạp vào là lượng calo bạn có được từ thức phẩm sau khi ăn, trong khi calo tiêu thụ là lượng calo mà bạn sẽ đốt cháy.  Qúa trình tiêu thụ calo xảy ra khi quá trình trao đổi chất hoạt động, tiêu hóa và vận động. Cân nặng của bạn ổn định khi lượng calo nạp vào tương đương với calo tiêu thụ.  Do vậy, để giảm cân, bạn cần ăn ít calo hơn. Việc tính lượng calo cần thiết cho cơ thể trở nên quan trọng hơn bao giờ hết để xác định bạn sẽ cần bao nhiêu calo rồi cắt giảm đi để nhắm tới mục đích giảm cân.', '1'),
(7, 1, '#giamcan', 'Bữa trưa khoẻ', 'Calo nạp vào là lượng calo bạn có được từ thức phẩm sau khi ăn, trong khi calo tiêu thụ là lượng calo mà bạn sẽ đốt cháy.  Qúa trình tiêu thụ calo xảy ra khi quá trình trao đổi chất hoạt động, tiêu hóa và vận động. Cân nặng của bạn ổn định khi lượng calo nạp vào tương đương với calo tiêu thụ.  Do vậy, để giảm cân, bạn cần ăn ít calo hơn. Việc tính lượng calo cần thiết cho cơ thể trở nên quan trọng hơn bao giờ hết để xác định bạn sẽ cần bao nhiêu calo rồi cắt giảm đi để nhắm tới mục đích giảm cân.', '1'),
(8, 1, '#giamcan', 'Bữa tối ít calo', 'Calo nạp vào là lượng calo bạn có được từ thức phẩm sau khi ăn, trong khi calo tiêu thụ là lượng calo mà bạn sẽ đốt cháy.  Qúa trình tiêu thụ calo xảy ra khi quá trình trao đổi chất hoạt động, tiêu hóa và vận động. Cân nặng của bạn ổn định khi lượng calo nạp vào tương đương với calo tiêu thụ.  Do vậy, để giảm cân, bạn cần ăn ít calo hơn. Việc tính lượng calo cần thiết cho cơ thể trở nên quan trọng hơn bao giờ hết để xác định bạn sẽ cần bao nhiêu calo rồi cắt giảm đi để nhắm tới mục đích giảm cân.', '1'),
